function (component)
        {
            var c = componentSet.add(component);
            $scope.currentComponent = c;
            if(c != null){
                $scope.designs = c.designs;
                $scope.views = c.views;
                $scope.controllers = c.controllers;
                // $state.go("root.views.properties",{label:c.label, type:c.type, num:c.num});
            }
        }

function(component){
            $state.go("root.views.properties", {label: component.label, type: component.type, num: component.num});
        }

function (action, component) {

       switch(action.todo){
        case 1:
         $scope.up(component);
         break;
        case 2:
         $scope.down(component);
         break;
        case 3:
         $scope.clone(component);
         break;
        case 4:
         $scope.cut(component);
         break;
        case 5:
         $scope.delete(component);
         break;
        default:
         console.log("unknown case option");
       }

      }

